package introåoJava;

public class RelationOperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10, y = 5;
		System.out.println("x > y : " + (x > y)); // true
		System.out.println("x < y : " + (x < y)); // false
		System.out.println("x >= y : " + (x >= y)); // true
		System.out.println("x <= y : " + (x <= y)); // false
		System.out.println("x == y : " + (x == y)); // false
		System.out.println("x != y : " + (x != y)); // true
	}

}
